DROP TABLE IF EXISTS "KINDER";
CREATE TABLE [KINDER] ([KinderID] INTEGER PRIMARY KEY AUTOINCREMENT NULL, [Vorname] VARCHAR(100) NULL, [Vorname2] VARCHAR(100) NULL, [Nachname] VARCHAR(100) NULL, [GebName] VARCHAR (100) NULL, [Geburtstag] DATE NULL, [Kirche] VARCHAR(100) NULL, [PersonenID] INTEGER NULL);
DROP TABLE IF EXISTS "BESUCH";
CREATE TABLE [BESUCH] ([BesuchID] INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, [BesuchsDatum] DATE NULL, [BesuchsGrund] VARCHAR(255) NULL, [Mitbringsel] VARCHAR(100) NULL, [Bibeltext] VARCHAR(100) NULL, [Memo] TEXT NULL, [Terminabsprache] VARCHAR(100) NULL, [PersonenID] INTEGER NULL);
DROP TABLE IF EXISTS "KOMMUNIONEN";
